package com.DecisionMakingExamples;

public class IfElseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int var1=30;
		 int var2=20;
		 
		 System.out.println("The Value of Variable var1 is "+var1);
		 System.out.println("The Value of Variable var2 is "+var2);
		 
		 //Comparing Var1 variable value with var1 variable value
		 
		 if(var1>var2)
		 {		// True Block
			 System.out.println(" var1 variable value is greater than var2 variable value");
		 }	
		 else
		 {
			// false Block
			 System.out.println("System.out.println(\" var1 variable value is less than var2 variable value \");");
		 }
		 
			
	}
	}

