package com.LoopsExamples;

public class ForLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*Syntax: (initialization;condition;increment or Decrement*/
		for(int i=1;i<=4;i++)
		{
			System.out.println(i+ "Webdriver");
		}
System.out.println("***********");
		for(int i=2;i<=4;i=i+2)
		{
			System.out.println(i+ "selenium");
		}
	System.out.println("+++++++++++++++");

		for(int i=-2; i<=-5; i--)//
		{
			System.out.println(i+ "Load");
		}
		System.out.println("$$$$$$$$$$$$");
		
		for(int i=-3;i>=-9; i=i-3)
		{
			System.out.println(i+ "Hedya");
		}
	System.out.println("###############");
	
	
	
	
	
	
	
	}

}
