package com.LoopsExamples;

public class WhileLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

/*Syntax: (initialization)
   	condition //if condition true
   	{
 	system.out.prntln("")
 	 	increment or Decrement
 	}						*/
		int i=2;
		while(i<=3)
		{
			System.out.println(i+ "While loop Example");
			i++;
		}
		System.out.println("*****************");
		System.out.println(i+ "Variable value is ");
		
		while(i<=6)
		{
			System.out.println(i+ "valriable value is");
			i++;
		}
	}

}

